mapper.insert:
INSERT INTO COMMUNITY(
					   COM_NO
					 , USER_NO
					 , COM_TITLE
					 , SALAD_NAME
					 , SALAD_MATERIAL
					 , SALAD_SAUCE
					 , SALAD_COMMENT
					 )
					 VALUES
					 (
					 SEQ_CMNO.NEXTVAL
					 , #{userNo}
					 , #{comTitle}
					 , #{saladName}
					 , #{saladMaterial}
					 , #{saladSauce}
					 , #{saladComment}
					 )

INSERT INTO REPLY(
				   REPLY_NO
				 , USER_NO
				 , REF_COM_NO
				 , REPLY_CONTENT
				 )
				 VALUES
				 (
				 SEQ_RPNO.NEXTVAL
				 , #{userNo}
				 , #{refComNo}
				 , #{replyContent}
				 )

mapper.select:
		SELECT CASE WHEN COM_NO = #{comNo} THEN 'Y'  
                     ELSE 'N'  
                     END MY_LIKE
		FROM "LIKE"
		WHERE USER_NO = #{userNo}
		  AND COM_NO = #{comNo}

SELECT COUNT(COM_NO) LIKE_COUNT
FROM "LIKE"
WHERE COM_NO = #{comNo}

mapper.insert:
INSERT INTO ATTACHMENT(
						FILE_NO
					  , ORIGIN_NAME
					  , CHANGE_NAME
					  , UPLOAD_DATE
					  , REF_CNO
					  )
					  VALUES
					  (
					    SEQ_ATNO.NEXTVAL
					  , #{originName}
					  , #{changeName}
					  , SYSDATE
					  , #{comNo}
					  )

mapper.update:
UPDATE 
		COMMUNITY
   SET 
   		COM_TITLE = #{comTitle}
   	  , SALAD_NAME = #{saladName}
   	  , SALAD_MATERIAL = #{saladMaterial}
   	  , SALAD_SAUCE = #{saladSauce}
   	  , SALAD_COMMENT = #{saladComment}
   	  , MODIFY_DATE = SYSDATE
WHERE
		COM_NO = #{comNo}

mapper.delete:
UPDATE COMMUNITY
   SET STATUS = 'N'
WHERE COM_NO = #{cno}
