mapper.update:
UPDATE NOTICE
SET COUNT = NVL(COUNT, 0) + 1
WHERE NOTICE_NO = #{nno}

UPDATE
		NOTICE
   SET
   		CATEGORY = #{category}
   	  , TITLE = #{title}
   	  , CONTENT = #{content}
   	  , SHOW = #{show}
   	  , TOP = #{top}
 WHERE
 		NOTICE_NO = #{noticeNo}

UPDATE
		ATTACHMENT
   SET 
		ORIGIN_NAME = #{originName}
	  , CHANGE_NAME = #{changeName}
	  , UPLOAD_DATE = SYSDATE
 WHERE
 		REF_NNO = #{refNno}

