mapper.select:
SELECT ORDER_NO
FROM "ORDER"
WHERE ORDERER = #{userNo}
 AND ORDER_DATE = (
 					SELECT MAX(ORDER_DATE)
 					FROM "ORDER"
 					WHERE ORDERER = #{userNo}
 					)
 AND ROWNUM = 1

SELECT COUNT(*) FROM ( SELECT * FROM ( SELECT A.*, ROW_NUMBER() OVER (PARTITION BY ORDER_NO ORDER BY STATUS DESC) RNUM FROM ( SELECT O.ORDER_NO, O.ORDER_ITEM, O.ORDER_DATE, O.STATUS, O.SELLER_NAME, O.PAY_METHOD, S.SELLER_IMAGEPATH FROM "ORDER" O JOIN SELLER S ON (O.SELLER = S.USER_NO) WHERE ORDERER = #{userNo}
AND O.SELLER_NAME LIKE '%' || #{keyword} || '%'
AND EXTRACT(YEAR FROM O.ORDER_DATE ) = #{year}
AND O.ORDER_DATE >= ADD_MONTHS(SYSDATE, #{month})
ORDER BY DECODE(O.STATUS, '상품준비중', 1, '배송중', 2, '배송완료', 3, '결제완료', 4, '결제취소', 5) ) A ) WHERE RNUM = 1 )B JOIN VW_ORDER_LIST V ON (V.ORDER_NO = B.ORDER_NO)

SELECT B.*, V.ORDER_PRICE, V.SELLER_NAME SELLER_COUNT, V.QUANTITY FROM ( SELECT * FROM ( SELECT A.*, ROW_NUMBER() OVER (PARTITION BY ORDER_NO ORDER BY STATUS DESC) RNUM FROM ( SELECT O.ORDER_NO, O.ORDER_ITEM, O.ORDER_DATE, O.STATUS, O.SELLER_NAME, O.PAY_METHOD, S.SELLER_IMAGEPATH FROM "ORDER" O JOIN SELLER S ON (O.SELLER = S.USER_NO) WHERE ORDERER = #{userNo}
AND O.SELLER_NAME LIKE '%' || #{keyword} || '%'
AND EXTRACT(YEAR FROM O.ORDER_DATE ) = #{year}
AND O.ORDER_DATE >= ADD_MONTHS(SYSDATE, #{month})
ORDER BY DECODE(O.STATUS, '상품준비중', 1, '배송중', 2, '배송완료', 3, '결제완료', 4, '결제취소', 5) ) A ) WHERE RNUM = 1 )B JOIN VW_ORDER_LIST V ON (V.ORDER_NO = B.ORDER_NO) ORDER BY B.ORDER_DATE DESC

SELECT COUNT(*)
FROM "ORDER" O
JOIN MEMBER M ON (ORDERER = USER_NO)
WHERE SELLER = #{userNo}

	AND M.USER_ID LIKE '%' || #{keyword} || '%' 


   	AND EXTRACT(YEAR FROM ORDER_DATE ) = #{year}


    AND ORDER_DATE >= ADD_MONTHS(SYSDATE, #{month})


	AND O.STATUS = #{status}

SELECT ORDER_DATE, ORDER_NO, ORDER_ITEM, ORDER_PRICE, QUANTITY, M.USER_ID ORDERER, O.STATUS, DELIVERY_NO, O.PRODUCT_NAME
FROM "ORDER" O
JOIN MEMBER M ON (ORDERER = USER_NO)
WHERE SELLER = #{userNo}

	AND M.USER_ID LIKE '%' || #{keyword} || '%' 


   	AND EXTRACT(YEAR FROM O.ORDER_DATE ) = #{year}


    AND O.ORDER_DATE >= ADD_MONTHS(SYSDATE, #{month})


	AND O.STATUS = #{status}

ORDER BY O.ORDER_DATE DESC

mapper.update:
UPDATE "ORDER"
SET STATUS = #{status}

	, DELIVERY_NO = #{deliveryNo}


	, DELIVERY_COMPLETE_DATE = SYSDATE
	, SETTLEMENT_EXPECTED_DATE = DECODE(TO_CHAR(SYSDATE, 'DY'), '일', SYSDATE + 1,
                                                            '월', SYSDATE + 1,
                                                            '화', SYSDATE + 1,
                                                            '수', SYSDATE + 1, 
                                                            '목', SYSDATE + 1, 
                                                            '금', SYSDATE + 3, 
                                                            '토', SYSDATE + 2)

WHERE SELLER = #{seller}
	AND ORDER_NO = #{orderNo}

mapper.select:
		SELECT A.*, SELLER_IMAGEPATH
		FROM (SELECT *
		        FROM "ORDER"
		        WHERE ORDER_NO = #{orderNo}
                    AND SELLER = #{seller}    
            	) A	
		JOIN SELLER ON(SELLER = USER_NO)

mapper.update:
UPDATE "MEMBER"
SET POINT = POINT - #{point}
WHERE USER_NO = #{userNo}

UPDATE "MEMBER"
SET POINT = POINT + #{usePoint}
WHERE USER_NO = #{orderer}
