mapper.select:
SELECT ORDER_NO
FROM "ORDER"
WHERE ORDERER = #{userNo}
 AND ORDER_DATE = (
 					SELECT MAX(ORDER_DATE)
 					FROM "ORDER"
 					WHERE ORDERER = #{userNo}
 					)
 AND ROWNUM = 1

SELECT COUNT(*) FROM ( SELECT * FROM ( SELECT A.*, ROW_NUMBER() OVER (PARTITION BY ORDER_NO ORDER BY STATUS DESC) RNUM FROM ( SELECT O.ORDER_NO, O.ORDER_ITEM, O.ORDER_DATE, O.STATUS, O.SELLER_NAME, O.PAY_METHOD, S.SELLER_IMAGE FROM "ORDER" O JOIN SELLER S ON (O.SELLER = S.USER_NO) WHERE ORDERER = #{userNo}
AND O.SELLER_NAME LIKE '%' || '${keyword}' || '%'
AND EXTRACT(YEAR FROM O.ORDER_DATE ) = ${year}
AND O.ORDER_DATE >= ADD_MONTHS(SYSDATE, ${month})
ORDER BY DECODE(O.STATUS, '상품준비중', 1, '배송중', 2, '배송완료', 3, '결제완료', 4, '결제취소', 5) ) A ) WHERE RNUM = 1 )B JOIN VW_ORDER_LIST V ON (V.ORDER_NO = B.ORDER_NO)

SELECT B.*, V.ORDER_PRICE, V.SELLER_NAME SELLER_COUNT, V.QUANTITY FROM ( SELECT * FROM ( SELECT A.*, ROW_NUMBER() OVER (PARTITION BY ORDER_NO ORDER BY STATUS DESC) RNUM FROM ( SELECT O.ORDER_NO, O.ORDER_ITEM, O.ORDER_DATE, O.STATUS, O.SELLER_NAME, O.PAY_METHOD, S.SELLER_IMAGE FROM "ORDER" O JOIN SELLER S ON (O.SELLER = S.USER_NO) WHERE ORDERER = ${userNo}
AND O.SELLER_NAME LIKE '%' || '${keyword}' || '%'
AND EXTRACT(YEAR FROM O.ORDER_DATE ) = ${year}
AND O.ORDER_DATE >= ADD_MONTHS(SYSDATE, ${month})
ORDER BY DECODE(O.STATUS, '상품준비중', 1, '배송중', 2, '배송완료', 3, '결제완료', 4, '결제취소', 5) ) A ) WHERE RNUM = 1 )B JOIN VW_ORDER_LIST V ON (V.ORDER_NO = B.ORDER_NO) ORDER BY B.ORDER_DATE DESC
