mapper.select:
SELECT COUNT(*)
FROM QUESTION
WHERE QUESTION_WRITER = #{questionWriter}
	AND QUESTION_SELLER = #{questionSeller}
	AND ORDER_NO = #{orderNo}
	AND STATUS = 'Y'

mapper.insert:
INSERT INTO QUESTION(
					QUESTION_NO
				  , QUESTION_WRITER
				  , QUESTION_SELLER
				  , CATEGORY
				  , ORDER_NO
				  , ANSWER_MODE
				  , QUESTION_TITLE
				  , QUESTION_CONTENT
				  )
			VALUES(
				SEQ_QNO.NEXTVAL
			  , #{questionWriter}
			  , #{questionSeller}
			  , #{category}
			  , #{orderNo}
			  , #{answerMode}
			  , #{questionTitle}
			  , #{questionContent}
			  )

INSERT INTO ANSWER(
					ANSWER_NO
				  , QUESTION_NO
				  , ANSWER_WRITER
				  , ANSWER_CONTENT
				  )
			VALUES(
					SEQ_ANNO.NEXTVAL
				  , #{questionNo}
				  , #{answerWriter}
				  , #{answerContent}
			)

INSERT INTO ATTACHMENT(
						FILE_NO
					  , ORIGIN_NAME
					  , CHANGE_NAME
					  , REF_QNO
					  )
			VALUES(
					SEQ_ATNO.NEXTVAL
				  , #{originName}
				  , #{changeName}
				  , SEQ_QNO.CURRVAL
				  )

INSERT INTO ATTACHMENT(
						FILE_NO
					  , ORIGIN_NAME
					  , CHANGE_NAME
					  , REF_ANO
					  )
			VALUES(
					SEQ_ATNO.NEXTVAL
				  , #{originName}
				  , #{changeName}
				  , SEQ_ANNO.CURRVAL
				  )

mapper.select:

SELECT COUNT(*)
FROM QUESTION Q
LEFT JOIN SELLER ON (QUESTION_SELLER = USER_NO)
WHERE QUESTION_WRITER = #{questionWriter} AND Q.STATUS = 'Y'

	AND TO_CHAR(CREATE_DATE, 'YYYY-MM-DD') BETWEEN #{startDate} and #{endDate}


	AND ANSWER = #{answer}


	AND CATEGORY = #{category}




			AND SELLER_NAME LIKE '%' || #{keyword} || '%'


			AND QUESTION_TITLE LIKE '%' || #{keyword} || '%'
			OR QUESTION_CONTENT LIKE '%' || #{keyword} || '%'


			AND ORDER_NO = #{orderNo}


SELECT QUESTION_NO, TO_CHAR(CREATE_DATE, 'YYYY-MM-DD HH24:MI:SS') "CREATE_DATE", QUESTION_SELLER, SELLER_NAME, CATEGORY, QUESTION_TITLE, ORDER_NO, ANSWER
FROM QUESTION Q
LEFT JOIN SELLER ON (QUESTION_SELLER = USER_NO)
WHERE QUESTION_WRITER = #{questionWriter} AND Q.STATUS = 'Y'

	AND TO_CHAR(CREATE_DATE, 'YYYY-MM-DD') BETWEEN #{startDate} and #{endDate}


	AND ANSWER = #{answer}


	AND CATEGORY = #{category}




			AND SELLER_NAME LIKE '%' || #{keyword} || '%'


			AND QUESTION_TITLE LIKE '%' || #{keyword} || '%'
			OR QUESTION_CONTENT LIKE '%' || #{keyword} || '%'


			AND ORDER_NO = #{orderNo}



ORDER BY CREATE_DATE DESC

mapper.update:
UPDATE QUESTION
SET ANSWER = 'Y'
WHERE QUESTION_NO = #{qno}

UPDATE ANSWER
SET SCORE = #{score}
WHERE ANSWER_NO = #{answerNo}
